decimal=9

def get_WETH(token_address):
    # token_address=web3.to_checksum_address('0xc0200b1c6598a996a339196259ffdc30c1f44339')
    token_address=web3.to_checksum_address(token_address)
    amount=1*10**decimal
    result = uni_route_contract.functions.getAmountsOut(amount, [token_address,WETH_address]).call()
    weth_price = utils.get_ethereum_price()
    # one_price=result[-1]/(10**18)*weth_price
    one_price=result[-1]/(10**18)*weth_price
    return one_price

def get_token_num(num):
    token_address=web3.to_checksum_address('0xc0200b1c6598a996a339196259ffdc30c1f44339')
    amount=num*10**18
    result = uni_route_contract.functions.getAmountsOut(amount, [WETH_address,token_address]).call()
    number=result[-1]/(10**decimal)
    return number

def get_USDC(token_address):
    token_address=web3.to_checksum_address('0xc0200b1c6598a996a339196259ffdc30c1f44339')
    amount=1*10**decimal
    result = uni_route_contract.functions.getAmountsOut(amount, [token_address,USDC_address]).call()
    one_price=result[-1]/(10**6)
    return one_price

